function readInput(e,a){13==a.keyCode&&(searchTerms=e.value,searchWikipedia())}function startSearch(){$("#first-screen").hide(),$("#results").empty().append('What would you like to search for? ===> <input type="search" id="searchInput" name="search" onkeydown="readInput(this, event)" autofocus>')}function searchWikipedia(){$.ajax(settings).done(function(e){var a=e.query.pages.title;console.log(a)})}var searchTerms,settings={async:!0,crossDomain:!0,url:"https://en.wikipedia.org/w/api.php?format=json&action=query&generator=search&gsrnamespace=0&gsrlimit=1&prop=extracts&pilimit=max&exintro&explaintext&exsentences=1&exlimit=max&gsrsearch="+searchTerms,type:"GET"};